import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FaHome } from 'react-icons/fa';
import { Dropdown } from 'react-bootstrap';
import '../styles/Navbar.css';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const closeDropdown = () => {
    setIsDropdownOpen(false);
  };

  useEffect(() => {
    // Add event listener to handle clicks outside the dropdown
    const handleOutsideClick = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        closeDropdown();
      }
    };

    document.addEventListener('click', handleOutsideClick);

    // Clean up the event listener when the component unmounts
    return () => {
      document.removeEventListener('click', handleOutsideClick);
    };
  }, []);

  return (
    <nav className={`navbar ${isMenuOpen ? 'open' : ''}`}>
      <div className="container">
        <Link className="navbar-brand" to="/">
          News<span>Quake</span>
        </Link>
        <div className="navbar-toggle" onClick={toggleMenu}>
          <div className="toggle-line"></div>
          <div className="toggle-line"></div>
          <div className="toggle-line"></div>
        </div>
        <ul className={`navbar-nav ${isMenuOpen ? 'open' : ''}`}>
          <li className="nav-item">
            <Link className="nav-link navbar-item" to="/">
              <FaHome /> Home
            </Link>
          </li>

          <li className="nav-item">
            <Dropdown show={isDropdownOpen} onToggle={toggleDropdown}>
              <Dropdown.Toggle as="a" variant="link" id="dropdown-basic">
                Categories
              </Dropdown.Toggle>
              <Dropdown.Menu ref={dropdownRef}>
                <Dropdown.Item as={Link} to="/category/politics" onClick={toggleMenu}>
                  Politics
                </Dropdown.Item>
                <Dropdown.Item as={Link} to="/category/sports" onClick={toggleMenu}>
                  Sports
                </Dropdown.Item>
                <Dropdown.Item as={Link} to="/category/entertainment" onClick={toggleMenu}>
                  Entertainment
                </Dropdown.Item>
                <Dropdown.Item as={Link} to="/category/technology" onClick={toggleMenu}>
                  Technology
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </li>

          <li className="nav-item">
            <Link className="nav-link navbar-item" to="/about">
              About
            </Link>
          </li>
          <li className="nav-item">
            <Link className="nav-link navbar-item" to="/contact">
              Contact
            </Link>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
